from pysnooper import snoop
import sys

class Solution:
    directions = [(0, -1), (-1, 0), (0, 1), (1, 0)]

    def exist(self, board, word: str):
        self.board = board
        self.row_num = len(self.board)
        if self.row_num == 0:
            return False

        self.column_num = len(self.board[0])
        if self.column_num == 0:
            return False

        self.word = word
        if len(self.word) > self.row_num * self.column_num:
            return False

        self.used = [[False for _ in range(self.column_num)] for _ in range(self.row_num)]
        for i in range(self.row_num):
            for j in range(self.column_num):
                    if self.surround(i, j, 0):
                        return True

        return False


    def surround(self, i, j, depth):
        if depth == len(self.word) - 1:
            print(f'({i},{j})')
            return self.board[i][j] == self.word[depth]

        if self.word[depth] == self.board[i][j]:
            print(f'({i},{j})', end=' ')
            self.use_add(i, j)
            for direction in self.directions:
                x = i + direction[0]
                y = j + direction[1]

                if x < 0 or x > self.row_num - 1:
                    continue
                if y < 0 or y > self.column_num - 1:
                    continue
                if not self.isused(x, y) and self.surround(x, y, depth+1):
                    return True

            self.use_remove(i, j)

        return False


    def use_add(self, i, j):
        self.used[i][j] = True

    def use_remove(self, i, j):
        self.used[i][j] = False

    def isused(self, i, j):
        return self.used[i][j]


